import React from "react";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { Link, NavLink } from "react-router-dom";
import { getDiets } from "../redux/actions";
import { getRecipes } from "../redux/actions";
import { filterByDiet } from '../redux/actions'
import SearchBar from "./SearchBar";
import Style from '../styles/NavBar.module.css'

import logo from '../imagenes/cooking.png'

export default function NavBar(){
  const dispatch=useDispatch();
  const diets =useSelector((state)=> state.diets);
  const allRecipes =useSelector((state) =>  state.recipes);
  const [currentPage,setCurrentPage] =useState(1);
  const [recipePerPage,setRecipePerPAge] =useState(9);
  const indexOfLastRecipe= currentPage*recipePerPage;
  const indexOfFirstRecipe= indexOfLastRecipe-recipePerPage;
  const currentRecipes =allRecipes.slice(indexOfFirstRecipe,indexOfLastRecipe);
  
  useEffect ( () => {
    dispatch(getDiets())
    dispatch(getRecipes())
    
  },[dispatch]);
  
  function handleFilterDiets(e){
    e.preventDefault();
    dispatch(filterByDiet(e.target.value))
  }
  
  function handleClick(e){
    e.preventDefault();
    dispatch(getRecipes());
  }
  return(
    <header>
      <nav className={Style.navbar}>
        <ul className={Style.list}>
          <li className={Style.item}>
            <NavLink className={Style.home} exact to='/'>
              <img
                className={Style.imgHome}
                id='logorecipe'
                src={logo}
                width="30"
                height="30"
                alt=""
              />
            </NavLink>
            <div className={Style.t_f_s}>
              <h1 className={Style.title} >Foods-Recipes </h1>
              <div className={Style.filtros_search}>
                <div className={Style.select_button}>
                  <div className={Style.filtros}>
                    <div>
                      <label className={Style.label}>Diets Types
                      </label>
                      <select className={Style.select} onChange={(e) => handleFilterDiets(e) }>
                        <option value='All'>All</option>
                        {diets && diets.map((d)=>(
                          <option value={d.name} key={d.name}>{d.name}</option>
                         ))
                        }
                      </select>
                    </div>
                  </div>
                  <button className={Style.boton} onClick={e => {handleClick(e)}}>Refresh Recipes</button>    
                </div>
                <SearchBar />      
                </div>
            </div>
            <Link className={Style.newRecipe} to='recipe'>Create Recipe</Link>          
          </li>
        </ul>
      </nav>
    </header>
  )
}
