const initialState={
  recipes: [],
  allRecipes: [],
  diets: [],
}
export default function rootReducer(state= initialState,action){
  switch(action.type){
    case 'GET_RECIPES':
      return {
        ...state,
        recipes: action.payload,
        allRecipes: action.payload
      }
    case 'GET_DIETS':
      return  {
        ...state,
        diets: action.payload,
      }
    case 'FILTER_BY_DIET':
      const allRecipes = state.allRecipes;
      const dietsFilter=
      action.payload === 'All'
      ? allRecipes
      : allRecipes.filter(
        (el) =>
        el.diets &&
        !el.createInDb
        ? el.diets.find(e => e===action.payload)
        : el.Diets.find(e => e.name===action.payload)
      ) 
      return{
        ...state,
        recipes: dietsFilter 
      }
      default:
    return state;
  }
}
